using System.Collections.Generic;
using System.Threading.Tasks;
using Armyknife.Exceptions;
using Armyknife.Resources;
using Armyknife.Models;
using Armyknife.Business.Interfaces;

namespace Armyknife.Tools.Implementations
{
   internal class [TOOLCLASSNAME] : I[TOOLTYPE]
   {
      public string Name => "[TOOLKEY]";

      public string Description => ToolResources.[TOOLNAME]Description;

      public string Category => CategoryResources.[CATEGORYNAME]Category;

      public string HelpText => ToolResources.[TOOLNAME]Help;

      public bool ShowToolInHelp => true;

      public async Task<string> ExecuteAsync(IDictionary<string, string> args)
      {
         // TODO remove this, this is only used to let the build succeed
         await Task.CompletedTask;
         if (!args.ContainsKey(Constants.InputKey))
         {
            throw new ArmyknifeException(ExceptionResources.NoInput);
         }

         return string.Empty;
      }
   }
}

using Armyknife.Exceptions;
using Armyknife.Models;
using Armyknife.Tools.Implementations;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using System;
using System.Collections.Generic;
using System.Threading.Tasks;

namespace Armyknife.Tools.Tests.Implementations
{
   [TestClass]
   public class [UNITTESTCLASSNAME]
   {
      private [TOOLCLASSNAME] _tool;

      [TestInitialize]
      public void Initialize()
      {
         _tool = new [TOOLCLASSNAME]();
      }

      [TestCleanup]
      public void Cleanup()
      {
         // Empty for now
      }

      [TestMethod]
      public async Task [TOOLCLASSNAME]_ExecuteAsync_NoInput_ShouldThrowArmyknifeException()
      {
         // arrange
         var argsDictionary = new Dictionary<string, string>();

         // act / assert
         await Assert.ThrowsExceptionAsync<ArmyknifeException>(() => _tool.ExecuteAsync(argsDictionary));
      }

      [TestMethod]
      public async Task [TOOLCLASSNAME]_ExecuteAsync_HappyFlow()
      {
         // arrange
         var argsDictionary = new Dictionary<string, string>
         {
            { Constants.InputKey, Guid.NewGuid().ToString() }
         };

         // act
         string output = await _tool.ExecuteAsync(argsDictionary);

         // assert
         Assert.AreEqual(string.Empty, output);
      }
   }
}

